package equals;

public class Book
{
	private String name;
	private Employee owner;
	
	public Book()
	{
		// TODO Auto-generated constructor stub
	}

	public String getName()
	{
		return name;
	}

	public void setName(String name)
	{
		this.name = name;
	}

	public Employee getOwner()
	{
		return owner;
	}

	public void setOwner(Employee owner)
	{
		this.owner = owner;
	} 
}
